const Validation = (gameData)=>{
  let errors={}
  // Errores en nombre
  if(!gameData.name) {
    errors.name='Name is Required';
  }
  else if(gameData.name.length < 3){
    errors.name='The name is too short!'
  }
  else if (gameData.name.length > 70) {
    errors.name='The name is too large';
  }else errors.name= ''
  // Errores en descripcion
  if (!gameData.description) {
    errors.description='The game needs a description';
  }
  else if(gameData.description.length <= 10){
    errors.description='The description is too short!'
  }
  else if (gameData.description.length >= 401) {
    errors.description='The description is too large!'
  }else errors.description=''
  // Errores en rating
  if (gameData.rating < 0) {
    errors.rating="Even if you don't like the game, minimum rating is 0" ;
  }
  else if (gameData.rating > 5) {
    errors.rating='Sadly the max rate is 5';
  }else errors.rating=''

}

export default Validation;
