import React, { useState, useEffect } from 'react';

const Paginate = (allvideogames) => {
  const limit = 15;
  const pages = Math.ceil((allvideogames?.length || 0) / limit);
  const [currentPage, setCurrentPage] = useState(1);
  const [currentGames, setCurrentGames] = useState([]);

  useEffect(() => {
    const startIndex = (currentPage - 1) * limit;
    const endIndex = startIndex + limit;
    const gamesToShow = allvideogames?.slice(startIndex, endIndex) || [];
    setCurrentGames(gamesToShow);
  }, [allvideogames, currentPage]);

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  return (
    <div>
      <h2>Current Page: {currentPage}</h2>
      <ul>
        {currentGames.map((game, index) => (
          <li key={index}>{game}</li>
        ))}
      </ul>
      <button onClick={handlePrevPage} disabled={currentPage === 1}>
        Previous Page
      </button>
      <button onClick={handleNextPage} disabled={currentPage === pages}>
        Next Page
      </button>
    </div>
  );
};

export default Paginate;
