const {useState } = require("react")

const Paginate = ({allVideogames})=>{

  const gamesPerPage= 15
  const maxPage = Math.ceil(allVideogames.length/gamesPerPage)
  const minPage =1
  const pageNumbers= Array.from({ length: maxPage }, (_, index) => index + 1);

  const [currentPage, setCurrentPage]= useState(1)
  console.log(currentPage);
  const indexOfLastGame= currentPage*gamesPerPage
  const indexOfFirstGame= indexOfLastGame-gamesPerPage
  const currentGames= allVideogames.slice(indexOfFirstGame, indexOfLastGame)

  const handleOnClick= (event)=>{
    if(event.target.value !== currentPage)
    setCurrentPage(event.target.value)
  }

  const handleNext= (event)=>{
    if(currentPage !== maxPage){
      currentPage++
      setCurrentPage(currentPage)
    }
  }

  const handlePrev= event=>{
    if(event.target.value !== minPage){
      currentPage--
      setCurrentPage(currentPage)
    }
  }
  return(
    <div>
      <button key='P1' onClick={handlePrev}>Prev</button>
      {pageNumbers?.map(e=><button key={e} onClick={handleOnClick}>{e}</button>)}
      <button key='N1' onClick={handleNext}>Next</button>
    </div>
  )
}
export default Paginate
