import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getGenres } from "../../redux/actions";
import Validation from "./Validation";

const Form = () => {
  // Constantes y variables
  const dispatch = useDispatch();
  let allGenres= useSelector(state=>state.genres)

  let z= 1
  // Estados
  const [gameData, setGameData] = useState({
    name: "",
    description: "",
    release: "",
    image: "",
    rating: '',
    platforms: "",
    created: true,
    genres: [],
  });

  const [error, setError] = useState({
    name: "",
    description: "",
    release: "",
    image: "",
    rating: "",
    platforms: "",
    genres: "",
  });
  const [genres, setGenres]=useState([])


  useEffect(() => {
    dispatch(getGenres());
  }, [dispatch]);



//handlers
const handleOnChange = (event) => {
    setGameData({
      ...gameData,
      [event.target.name]: event.target.value
    });

    Validation({...gameData, [event.target.name]:event.target.value}, allGenres, error, setError)
  
}


const handleOnSubmit= (event)=>{
    event.preventDefault();
}


const handleGenresChanges= event=>{
  if(event.target.checked){
    setGenres([...genres, event.target.value])
    setGameData({...gameData, genres:[...genres, event.target.value]})
  }
}

  return (
    <form onSubmit={handleOnSubmit}>
      <label htmlFor="Name">Name</label>
      <input type="text" name="name" value={gameData.name} onChange={handleOnChange} />
      <p>{error.name}</p>
      <hr />
      <label htmlFor="Release">Release</label>
      <input type="date" name="release" onChange={handleOnChange} value={gameData.release} />
      <p>{error.release}</p>
      <hr />
      {allGenres.map((e) => (
                <div >
                  <ul >
                    <li>
                      <input
                        onChange={handlegenresChange}
                        type="checkbox"
                        id={`checkbox${e.id}`}
                        value={e.name}
                      />
                      <label for={`checkbox${e.id}`}>{e.name}</label>
                    </li>
                  </ul>
                </div>
              ))}
      <hr />
      <label htmlFor="Description">Description</label>
      <textarea type="text"name="description"placeholder="Write a description here"onChange={handleOnChange} value={gameData.description}
      ></textarea>
      <p>{error.description}</p>
      <hr />
      <label htmlFor="Image">Image</label>
      <input type="text" name="image" onChange={handleOnChange} value={gameData.image} />
      <hr />
      <label htmlFor="Rating">Rating</label>
      <input type="number" name="rating" onChange={handleOnChange} value={gameData.rating} />
      <p>{error.rating}</p>
      <hr />
      <label htmlFor="">Platforms</label>
      <input type="text" onChange={handleOnChange} value={gameData.platforms} />
      <hr />

      <button type="submit" >Submit!</button>
    </form>
  );
      }
export default Form;
