import {GET_GAMES, GET_GAMES_BY_ID, GET_GENRES, POST_GAMES, ORIGIN_FILTERED_GAMES, GENRE_FILTERED_GAMES, RATING_ORDERED_GAMES, LETTERS_ORDERED_GAMES, GET_GAMES_BY_NAME } from './types'

const initialState={
    allVideogames:[],
    genres:[],
    filteredGames: [],
    detail: {}
}

//----en la siguiente linea inicio el reducer, pasandole por parametros el stado inicial y las  destructuradas--//
const reducer= (state = initialState, {type, payload})=>{

// utilizo un switch para no hacer "if" encadenados o en cascadas.
    switch(type){
///casos de recuperacion de datos o de carga de los mismos
        case GET_GAMES:
            return{
            ...state,
            allVideogames:payload,
            filteredGames:payload,

        }
        case GET_GAMES_BY_NAME:
            return{
                ...state,
                filteredGames:payload
            }
        case GET_GAMES_BY_ID:
            return{
                ...state,
                detail:payload,
            }
        case GET_GENRES:
            return{
                ...state,
                genres:payload
            }
        case POST_GAMES:
            return{
                ...state,
            }
//casos de ordenamiento o filtrado
case GENRE_FILTERED_GAMES:
          const genderFilter = state.allVideogames.filter(game => game.genres.some(genre => genre === payload));
            return{...state, videogames:payload === "All" 
              ? [...state.allVideogames] 
              : genderFilter
            }


        case ORIGIN_FILTERED_GAMES:
          let filteredOriginGames = [];
            if (payload === "API") filteredOriginGames = state.allVideogames.filter(game => !isNaN(game.id));
            if (payload === "DB") filteredOriginGames = state.allVideogames.filter(game => isNaN(game.id));
            if (payload === "All" )filteredOriginGames = [...state.allVideogames];
              return {...state, videogames: filteredOriginGames};


        case LETTERS_ORDERED_GAMES:
          let sortedByName = [];
            if(payload === 'A-Z') sortedByName = [...state.allVideogames].sort((a, b) => a.name.localeCompare(b.name))
            if(payload === 'Z-A') sortedByName = [...state.allVideogames].sort((a, b) => b.name.localeCompare(a.name))
            if(payload === 'None') sortedByName = [...state.allVideogames]
              return {...state, videogames: sortedByName};



        case RATING_ORDERED_GAMES:
          let sortedByRating = [];
            if(payload === 'A') sortedByRating = [...state.allVideogames].sort((a, b) => b.rating - a.rating)
            if(payload === 'D') sortedByRating = [...state.allVideogames].sort((a, b) => a.rating - b.rating)
            if(payload === 'None') sortedByRating = [...state.allVideogames]
              return {...state, videogames: sortedByRating};
        default:
           return { ...state};
    }
}

export default reducer;
