import {Link} from 'react-router-dom'
import style from './Card.module.css'

const Card = ({name, genres, image, rating})=>{

    return(
        <div className={style.container} >
            <Link>
            <h1>Name</h1>
            <h2 >{name}</h2>
            <img className={style.image} src={image} alt={`Image of ${name}`} />
            <h1 >Genres</h1>
            {
            genres?.map(genre => (
            <p>{genre.name}</p>
            ))
            }
             <h1>Rating</h1>
             {rating && <p>{rating}</p>}
            </Link>
        </div>
    )
}

export default Card
