import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import {getGamesById} from '../../redux/actions'
const Detail = ()=>{
    let created = 'This games comes from database'
    let api = 'This games comes from an API'
    const dispatch= useDispatch();
    let {id}=useParams()

    useEffect(()=>{
        dispatch(getGamesById(id))
    }, [id])

    const detail=useSelector(state=>state.detail)
    if(isNaN(detail.id)) 
    

    return(
        <div>
            <img src={detail.image} alt={`Image of ${detail.name} not found`} />
            <h1>Id</h1>
            <p>{detail.id}</p>
            <h1>Name</h1>
            <p>{detail.name}</p>
            <h1>Platforms</h1>
            {detail?.map(platforms=><p key= {'a'+z++} value={platforms.name}>{platforms.name} </p>)}
            <h1>Description</h1>
            <h1>Released</h1>
            <h1>Raiting</h1>
            <h1>Genres</h1>
        </div>
    )

}

export default Detail
