import { Link } from "react-router-dom"
import SearchBar from "../SearchBar/SearchBar"
import { useLocation } from "react-router-dom"
import { useState } from "react"


const Nav = ()=>{
    const location= useLocation()
    const [disableForm, setDisableForm]= useState(false)
    const [disableHome, setDisableHome]=useState(true)

    const handleForm=(event)=>{
        if(location.pathname !== '/form'){
            setDisableForm(false)
        }else{setDisableForm(true)}

    }

    const handleHome= (event)=>{
        if(location.pathname !== '/home'){
            setDisableHome(false)
        }else{setDisableHome(true)}
    }

return (
    
    <nav>
        <SearchBar />
        
        <Link to= '/form'> <button onClick={handleForm} disabled={disableForm}>Submit new Game</button></Link>

        <Link to= '/home'> <button onClick={handleHome} disabled={disableHome}>Home</button></Link>
    </nav>
)
}
export default Nav
