import { useEffect, useState } from "react"

const Paginate = (allVideogames)=>{
const limit = 15
const pages = Math.ceil(allVideogames.length /limit)
const [positionI, setPositionI]=useState(1)
const [positionF, setPositionF]=useState(limit)
const [page, setPage]= useState(Math.ceil(positionF/limit))
const [videogamesP, setVideogamesP]=useState([])

useEffect(()=>{
  if(allVideogames.length>0){
    setPage(Math.ceil(positionF/limit))
    setVideogamesP(allVideogames.slice(positionI, positionF))
  }
}, [allVideogames, positionI, positionF])

const next =()=>{
  if(positionF<=allVideogames.length-1){
    setPositionI(positionI+limit)
    setPositionF(Math.min(positionF+limit, allVideogames.length))
  }
}

const prev =()=>{
  if (positionI>limit-1){
    setPositionI(positionI-limit)
    setPositionF(page === pages?limit*(pages-1):positionF-limit)
  }
}
const goPage =(event)=>{
  if(positionI > limit-1 && positionF<= allVideogames.length-1){
    setPositionI((event.target.value-1)*limit)
    setPositionF(parseInt((event.target.value-1)*limit)+limit)
  }
}
return(
  <div>
    <button onClick={prev}>Prev</button>
    <button onClick={next}>Next</button>
    <div>Pages{page} of {pages}</div>
  </div>
)

}
export default Paginate
