import Card from "../Card/Card";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import {getGame} from '../../redux/actions'


const Home = () =>{
    const dispatch = useDispatch()
    
    // useEffect(()=>{
    //     dispatch(getGame())
    // }, []) 
    let videogames=useSelector((state)=>state.allVideogames)


    let genres= videogames.flatMap((game) => game.genres.map(genres=>genres.name))
    


    return(
        <div>
            {
                videogames.map(({id, name, image, genres})=>{
                    return(
                        <Card 
                            key={id}
                            image={image}
                            name={name}
                            genres={genres?.name}
                        />
                    )
                })
            }
        </div>
    )

}

export default Home
