const regex = /^[a-zA-Z0-9 ,.()?!¿¡-]*$/;


const Validation = (gameData, error, setError, allGenres) => {
  const foundGenre = allGenres.find((genre) => gameData.genres.includes(genre));

  // Errores en nombre
  if (gameData.name && !regex.test(gameData.name)) {
    setError({ ...error, name: 'Invalid Name' });
  }
  if (gameData.name.length === 2) {
    setError({ ...error, name: 'The name is too short!' })
  }
  if (gameData.name.length > 70) {
    setError({ ...error, name: 'The name of the game is too large!' });
  }
  // Errores en descripcion
  if (gameData.description.length === 10) {
    setError({ ...error, description: 'The description is too short!' });
  }
  if (gameData.description.length >= 400) {
    setError({ ...error, description: 'The description is too large!' });
  }
  // Errores en rating
  if (gameData.rating < 0) {
    setError({ ...error, rating: "Even if you don't like the game, minimum rating is 0" });
  }
  if (gameData.rating > 5) {
    setError({ ...error, rating: 'Sadly the max rate is 5' });
  }

  // Errores en genres
  if (!foundGenre) {
    setError({ ...error, genres: `You need to choose at least one of the following valid genres ${allGenres}` });
  }
  if (gameData.genres.length > 3) {
    setError({ ...error, genres: "There's too many genres!" });
  }
}

export default Validation;
