import Card from "../Card/Card";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import {filterGamesByGenre, getGame, getGenres} from '../../redux/actions'
import Style from './Home.module.css'


const Home = () =>{
    const dispatch = useDispatch()
    
    useEffect(()=>{
        dispatch(getGame())
    }, []) 
    let videogames=useSelector((state)=>state.allVideogames)
   
    useEffect (()=>{
        dispatch(getGenres)
    },[])

    const handleFilter=(event)=>{
        dispatch(filterGamesByGenre(event.target.value))
    }
    let genresFilter= useSelector(state=>state.genres)

    return(
        <div className={Style.wrapper}>
            <div>
                <select >
                    <option value={'All'}>All</option>
                    <option value={event.name}>{event.name}</option>
                </select>
            </div>
            <div className={Style.container}>
            {
               videogames.map(({id, name, image, genres})=>{
                    return(
                        <Card 
                            key={id}
                            image={image}
                            name={name}
                            genres={genres}
                        />
                    )
                })
            }
            <div></div>
            </div>
        </div>
    )

}

export default Home
